// import logo from './logo.svg';


import { ZoomMtg } from '@zoomus/websdk';

import express from 'express';


// Uncomment this line if you are Building for Relative Paths (example: http://mywebsite.com/relativepath") and have set the "homepage" value in package.json - More info here: https://create-react-app.dev/docs/deployment/#building-for-relative-paths
// ZoomMtg.setZoomJSLib(process.env.PUBLIC_URL + '/node_modules/@zoomus/websdk/dist/lib', '/av')

ZoomMtg.preLoadWasm();
ZoomMtg.prepareJssdk();

// Поднимаем сервер
const app = express()
const port = 3000

app.get("/join", function(req, res) {

  res.send("Got join reques")

  var signatureEndpoint = 'http://localhost:4000'
  var apiKey = '2dSCUOCIRCm0-aZ2OLehJA'

  var meetingNumber = window.meetingNumber
  var passWord = window.meetingPass
  var role = window.role
  var leaveUrl = window.leaveUrl
  var userName = window.userName
  var userEmail = window.userEmail

  getSignature(
    signatureEndpoint,
    apiKey,
    meetingNumber,
    passWord,
    role,
    leaveUrl,
    userName,
    userEmail,
  )

})

function getSignature(
  signatureEndpoint,
  apiKey,
  meetingNumber,
  passWord,
  role,
  leaveUrl,
  userName,
  userEmail,
) {

  fetch(signatureEndpoint, {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify({
      meetingNumber: meetingNumber,
      role: role
    })
  }).then(res => res.json())
  .then(response => {
    startMeeting(
      response.signature,
      leaveUrl,
      meetingNumber,
      userName,
      apiKey,
      userEmail,
      passWord
      )
  }).catch(error => {
    console.error(error)
  })

}

function startMeeting(
  signature,
  leaveUrl,
  meetingNumber,
  userName,
  apiKey,
  userEmail,
  passWord
) {

  document.getElementById('zmmtg-root').style.display = 'block'

  ZoomMtg.init({
    leaveUrl: leaveUrl,
    isSupportAV: true,
    success: (success) => {
      console.log(success)

      ZoomMtg.join({
        signature: signature,
        meetingNumber: meetingNumber,
        userName: userName,
        apiKey: apiKey,
        userEmail: userEmail,
        passWord: passWord,
        success: (success) => {
          console.log(success)
        },
        error: (error) => {
          console.log(error)
        }
      })

    },
    error: (error) => {
      console.log(error)
    }
  })
}

app.listen(port, () => {
  console.log(`Express app listening at http://localhost:${port}`)
})
